def load_json():
    with open(JSON_PATH, 'r', encoding='utf-8') as f:
        return json.load(f)

def save_json(data):
    with open(JSON_PATH, 'w', encoding='utf-8') as f:
        json.dump(data, f, ensure_ascii=False, indent=2)

def main():
    print("Загрузка JSON файла...")
    data = load_json()

    # Найти последнюю главу (Раздел V) и добавить параграфы
    last_chapter = data['chapters'][-1]
    print(f"Текущий последний раздел: {last_chapter['title']}")
    print(f"Количество параграфов в нем: {len(last_chapter['paragraphs'])}")

    # Добавляем § 43 и § 44-45 в Раздел V
    print("\nДобавляем § 43 и § 44-45 в Раздел V...")
    for para in paragraphs_section_5:
        last_chapter['paragraphs'].append(para)
        print(f"  - Добавлен § {para['number']}: {para['title']}")

    # Добавляем Раздел VI
    print("\nДобавляем Раздел VI...")
    data['chapters'].append(section_6)
    print(f"  - Раздел VI добавлен с {len(section_6['paragraphs'])} параграфами")

    # Сохраняем
    print("\nСохранение JSON файла...")
    save_json(data)
    print("Готово!")

    # Статистика
    total_paragraphs = sum(len(ch['paragraphs']) for ch in data['chapters'])
    print(f"\nСтатистика:")
    print(f"  - Всего разделов: {len(data['chapters'])}")
    print(f"  - Всего параграфов: {total_paragraphs}")
    for i, ch in enumerate(data['chapters'], 1):
        print(f"    - Раздел {i} ({ch['title']}): {len(ch['paragraphs'])} параграфов")

if __name__ == "__main__":
    main()
